import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AccountBalance from './AccountBalance';

class Credits extends Component {

  constructor() {
    super()
    this.state = {
      item: {
        description: '',
        amount: 0,
        date: ''
      }
    }
  }

  //handlechange func to be done
  handleChange = (e) => {
    const value = e.target.value
    this.setState({
      input: value
    });
  }

  //handleSubmit to be done
  handleSubmit = (e) => {

  }

  //gets date and time
  getDateAndTime = () => {
    var today = newDate();
    var date = (today.getMonth()+1)+ '-' + today.getDate() + '-' + today.getFullYear();
    var time = today.getHours() + ':' + today.getMinutes() + ':' + today.getSeconds();
    var dateTime = date + " " + time;
    document.getElementById("")
  }


  //complete render and return
  render() {
    return (
      <div>
        <div>
          <h1>Credits</h1>
        </div>
        <div>
          <Link to="/">Home</Link>
        </div>
        <div>
          <Link to="/Login">Log In</Link>
        </div>
        <div>
          <Link to="/userProfile">User Profile</Link>
        </div>
        <div>
          <Link to="/Debits">Debits</Link>
        </div>

        <AccountBalance accountBalance={this.props.accountBalance}/>

        <div className= "add-credits-area">
          <form onSubmit = {this.handleSubmit}>
            <div>
              <label htmlFor="description">Description: </label>
              <input type="text" name="description" onChange={this.handleChange} value={this.state.item.description} />
            </div>
            <div>
              <label htmlFor="amount">Amount: </label>
              <input type="number" name="amount" value={this.state.item.amount}/>
            </div>
            <div>
              <label>Time: </label>
              <input type="text" id="currentDateTime"></input>
            </div>
            <button>Add Credit</button>
          </form>
        </div>
      </div>
    )
  }
}

export default Credits;
